import * as api from '../api/index';
//logowanie
export const signin = (formData:any, router:any) => async (dispatch:any) => {
  try {
    const { data } = await api.signIn(formData);
    dispatch({ type: 'AUTH', data });
    router.push('/');
  } catch (error) {
    console.log(error);
  }
};
